import React, { useState } from 'react'

import ThrillerEditRow from './ThrillerEditRow'

import { movieArray, checkIfFunction } from '../propTypes/propTypes'

export default function ThrillerTable({ thrillerState, removeMovie, 
                            setThrillerState, updateThrillerState }) {

    const [flipEdit, setFlipEdit] = useState(false)
    const [editRowKey, setEditRowKey] = useState('')


    const activateRemoveMovie = (movieId) => {
        removeMovie(movieId)
    }

    const flipEditfFunction = (clickedKey) => {
        setFlipEdit(!flipEdit)
        setEditRowKey(clickedKey)
    }

    return (
        <table>
            <thead>
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Year</th>
                    <th>Personal rating</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {flipEdit ?
                    < ThrillerEditRow thrillerState={thrillerState} editRowKey={editRowKey}
                        removeMovie={removeMovie} setEditRowKey={setEditRowKey}
                        setThrillerState={setThrillerState} flipEditfFunction={flipEditfFunction}
                        setFlipEdit={setFlipEdit} updateThrillerState={updateThrillerState} focusInput={focusInput} inputFocus={inputFocus} />
                    :
                    thrillerState.map((p, i) =>
                        <tr key={p.id}>
                            <td>{i + 1}</td>
                            <td>{p.name}</td>
                            <td>{p.year}</td>
                            <td>{p.rating}</td>
                            <td>
                                <button onClick={() => flipEditfFunction(p.id)} >Edit</button>
                                <button onClick={() => activateRemoveMovie(p.id)}>Delete</button>
                            </td>
                        </tr>
                    )
                }
            </tbody>
        </table>
    )
}

ThrillerTable.propTypes = {
    thrillerState: movieArray,
    removeMovie: checkIfFunction,
    setThrillerState: checkIfFunction,
    updateThrillerState: checkIfFunction
}
