import React, { useState } from 'react'


export default function ThrillerEditRow({ thrillerState, editRowKey, removeMovie, 
                                            setEditRowKey, setThrillerState, flipEditfFunction, setFlipEdit, updateThrillerState}) {


    const [editRowFormInput, setEditRowFormInput] = useState({
        id: thrillerState.filter(p => p.id === editRowKey)[0].id,
        name: thrillerState.filter(p => p.id === editRowKey)[0].name,
        year: thrillerState.filter(p => p.id === editRowKey)[0].year,
        rating: thrillerState.filter(p => p.id === editRowKey)[0].rating,
    })

    const updateEditForm = (e) => {
        setEditRowFormInput({
            ...editRowFormInput,
            [e.target.name]:e.target.value
        })
    } 

    const activateRemoveMovieFromEdit = (movieId) => {
        removeMovie(movieId)
    }

    const activateSetEditRowKeyFromEdit = (clickedKey) => {
        setEditRowKey(clickedKey)

        setEditRowFormInput({
            id: thrillerState.filter(p => p.id === clickedKey)[0].id,
            name: thrillerState.filter(p => p.id === clickedKey)[0].name,
            year: thrillerState.filter(p => p.id === clickedKey)[0].year,
            rating: thrillerState.filter(p => p.id === clickedKey)[0].rating
        })
    }

    const editSaveButton = (i) => {
        updateThrillerState(editRowFormInput, i)
        // setFlipEdit(false)

        // const arrayCopy = [...thrillerState]
        // arrayCopy.splice(i,1,editRowFormInput)
        // console.log(arrayCopy)
    }

    return (
        <>
            {thrillerState.map((m, i) =>
                (m.id === editRowKey) ?
                    <tr key={m.id}>
                        <td>{i + 1}</td>
                        <td><input type='text' name='name' value={editRowFormInput.name} onChange={updateEditForm} /></td>
                        <td><input type='number' name='year' value={editRowFormInput.year} onChange={updateEditForm} /></td>
                        <td><input type='number' name='rating' value={editRowFormInput.rating} onChange={updateEditForm} /></td>
                        <td>
                            <button type='button' onClick={() => editSaveButton(i)}>Save</button>
                            <button>Cancel</button>
                        </td>
                    </tr>
                    :
                    <tr key={m.id}>
                        <td>{i + 1}</td>
                        <td>{m.name}</td>
                        <td>{m.year}</td>
                        <td>{m.rating}</td>
                        <td>
                            <button onClick={() => activateSetEditRowKeyFromEdit(m.id)} >Edit</button>
                            <button onClick={() => activateRemoveMovieFromEdit(m.id)} >Delete</button>
                        </td>
                    </tr>
            )
            }
        </>
    )
}
