import React from 'react'

import { movieArray, checkIfFunction } from '../propTypes/propTypes'

import { useFormHooks } from '../customHook/formHook'

export const MovieForm = ({ addMovie, state}) => {

    const [thrillerForm, updateForm, reset] = useFormHooks({
        name: '',
        year: '',
        rating: ''
    })

                // const { email, password } = this.state;
                // const isEnabled =  email.length > 0 &&  password.length &gt; 0;

    const activateAddMovie = () => {
        addMovie(thrillerForm)

        reset()
    }

    console.log(thrillerForm.name.length)

    const addButonEnable = ( thrillerForm.name.length < 1 && thrillerForm.year.lenght < 1 && thrillerForm.rating.lenght < 1) ? true : false  

    if (state.length === 5) {
        return (
            <>
            </>
        )
    }
    else {
        return (

            <form>
                <label htmlFor='give-name'>Name</label>
                <input id='give-name' type='text' name='name' onChange={updateForm} value={thrillerForm.name} />

                <label htmlFor='give-year'>Year</label>
                <input id='give-year' type='number' name='year' onChange={updateForm} value={thrillerForm.year} />

                <label htmlFor='give-rating'>Rating</label>
                <input id='give-rating' type='number' name='rating' onChange={updateForm} value={thrillerForm.rating} />

                <button type='button' onClick={activateAddMovie}  disabled={addButonEnable} > Add </button>

            </form>
        )
    }
}

MovieForm.propTypes = {
    state: movieArray,
    addMovie: checkIfFunction
}
