import React, { useState } from 'react'

import { movieArray, checkIfFunction } from '../propTypes/propTypes'

// import { useFormHooks } from '../customHook/formHook'

export const MovieForm = ({ addMovie, state}) => {

    // const [thrillerForm, updateForm, reset] = useFormHooks({
    //     name: '',
    //     year: '',
    //     rating: ''
    // })

     const [thrillerForm, setThrillerForm] = useState({
         name: '',
         year: '',
         rating: ''
     })

    const updateForm = (e, max, min) => {
            setThrillerForm(
                {
                    ...thrillerForm,
                    [e.target.name]: (e.target.type === 'number') ? Number(e.target.value) : e.target.value
                })  
            }
    const updateRating = (e, min, max) => {
        const allowedNumbers = new RegExp(/^(?:[1-9]|0[1-9]|10)$/)

       console.log(allowedNumbers.test(e.target.value))

        setThrillerForm({
            ...thrillerForm,
            rating: allowedNumbers.test(e.target.value) ? e.target.value : e.target.value < min ? e.target.min : e.target.value > max ? e.target.max : ''
        })

        console.log(e.target.max)
        console.log(e.target.min)
    }



    const activateAddMovie = () => {
        addMovie(thrillerForm)

        setThrillerForm({
            name: '',
            year: '',
            rating: ''
        })
    }

    const addButonEnable = ((thrillerForm.name.length < 1) || (thrillerForm.year.length < 1) || (thrillerForm.rating.length < 1)) ? true : false  

    if (state.length === 5) {
        return (
            <>
            </>
        )
    }
    else {
        return (

            <form>
                <label htmlFor='give-name'>Name</label>
                <input id='give-name' type='text' name='name' onChange={updateForm} value={thrillerForm.name} />

                <label htmlFor='give-year'>Year</label>
                <input id='give-year' type='number' name='year' onChange={updateForm} value={thrillerForm.year}  />

                <label htmlFor='give-rating'>Rating</label>
                <input id='give-rating' type='number' onChange={updateRating} value={thrillerForm.rating} min='1' max='10' />

                <button type='button' onClick={activateAddMovie}  disabled={addButonEnable} > Add </button>

            </form>
        )
    }
}

MovieForm.propTypes = {
    state: movieArray,
    addMovie: checkIfFunction
}
