import React, { useState, useEffect } from 'react'


export default function ThrillerEditRow({ thrillerState, editRowKey, removeMovie, setEditRowKey }) {


    const [editRowFormInput, setEditRowFormInput] = useState({
        name: thrillerState.filter(p => p.id === editRowKey)[0].name,
        year: thrillerState.filter(p => p.id === editRowKey)[0].year,
        rating: thrillerState.filter(p => p.id === editRowKey)[0].rating,
    })


    const activateRemoveMovieFromEdit = (movieId) => {
        removeMovie(movieId)

        reupdateSetEditRowFormInput(movieId)
    }

    const reupdateSetEditRowFormInput = (movieId) => {
        console.log('clicked')
        setEditRowFormInput({
            name: thrillerState.filter(p => p.id === movieId)[0].name,
            year: thrillerState.filter(p => p.id === movieId)[0].year,
            rating: thrillerState.filter(p => p.id === movieId)[0].rating
        })
        console.log(movieId)

    }


    const activateSetEditRowKeyFromEdit = (clickedKey) => {
        setEditRowKey(clickedKey)
    }



    return (
        <>
            {thrillerState.map((m, i) =>
                (m.id === editRowKey) ?
                    <tr key={m.id}>
                        <td>{i + 1}</td>
                        <td><input type='text' value={editRowFormInput.name} /></td>
                        <td><input type='number' value={editRowFormInput.year} /></td>
                        <td><input type='number' value={editRowFormInput.rating} /></td>
                        <td>
                            <button>Save</button>
                            <button>Cancel</button>
                        </td>
                    </tr>
                    :
                    <tr key={m.id}>
                        <td>{i + 1}</td>
                        <td>{m.name}</td>
                        <td>{m.year}</td>
                        <td>{m.rating}</td>
                        <td>
                            <button onClick={() => activateSetEditRowKeyFromEdit(m.id)} >Edit</button>
                            <button onClick={() => activateRemoveMovieFromEdit(m.id)} >Delete</button>
                        </td>
                    </tr>
            )
            }
        </>
    )
}
