import React, { useState, useRef, useEffect, useCallback, memo } from 'react';

import {ThrillerForm} from './ThrillerForm'
import {ThrillerTable} from './ThrillerTable'

import {movieArray} from '../propTypes/propTypes'

export const  Thriller = memo(({ initialMovies })  => {

    const [thrillerState, setThrillerState] = useState(initialMovies.concat())
    const [flipEdit, setFlipEdit] = useState(false)

    console.log('Thriller has been rendered')

    const addMovie = (thrillerForm) => {
        setThrillerState([
            ...thrillerState,
            {
                id: (thrillerState.length === 0) ? 1 : Math.max(...thrillerState.map(p => p.id + 1)),
                ...thrillerForm
            }
        ])
    }

    const inputFocus = useRef()

    useEffect(() => {
        if (inputFocus.current) {
            inputFocus.current.focus()
        }
    })

    const removeMovie = useCallback((movieId) => {
        setThrillerState(
            thrillerState.filter(f => f.id !== movieId)
        )
    }, [thrillerState])

    const updateThrillerState = (arrayCopy) => {
        setThrillerState(arrayCopy)
    }

    return (
        <>
            <h3>Top 5 must see Thriller movies</h3>
            <ThrillerTable thrillerState={thrillerState} removeMovie={removeMovie}
                setThrillerState={setThrillerState} updateThrillerState={updateThrillerState} 
                setFlipEdit={setFlipEdit} flipEdit={flipEdit} ref={ flipEdit ? inputFocus : null} />
            { (thrillerState.length < 5) ? (< ThrillerForm addMovie={addMovie} thrillerState={thrillerState}
            flipEdit={flipEdit} 
            ref={ flipEdit ? null : inputFocus } />) : null }

        </>
    )
})

Thriller.propTypes = {
    initialMovies: movieArray
}
