import React, { useState } from 'react';

import {MovieForm} from './MovieForm'
import MovieTable from './MovieTable'

import {movieArray} from '../propTypes/propTypes'

export const  Movie = ({ initialMovies })  => {

    const [thrillerState, setThrillerState] = useState(initialMovies.concat())
    const [flipEdit, setFlipEdit] = useState(false)

    const addMovie = (thrillerForm) => {
        setThrillerState([
            ...thrillerState,
            {
                id: (thrillerState.length === 0) ? 1 : Math.max(...thrillerState.map(p => p.id + 1)),
                ...thrillerForm
            }
        ])
    }

    const removeMovie = (movieId) => {
        setThrillerState(
            thrillerState.filter(f => f.id !== movieId)
        )
    }

    const updateThrillerState = (arrayCopy) => {
        setThrillerState(arrayCopy)
    }

    return (
        <div id='thriller'>
            <h3>Top 5 must see Thriller movies</h3>
            <MovieTable thrillerState={thrillerState} removeMovie={removeMovie}
                setThrillerState={setThrillerState} updateThrillerState={updateThrillerState} 
                setFlipEdit={setFlipEdit} flipEdit={flipEdit} />

            < MovieForm addMovie={addMovie} thrillerState={thrillerState}
            flipEdit={flipEdit}  />
        </div>
    )
}

Movie.propTypes = {
    initialMovies: movieArray
}
