import React, { useState } from 'react';

import ThrillerForm from './ThrillerForm'
import ThrillerTable from './ThrillerTable'

export default function Thriller({initialMovies}) {

    const [thrillerState, setThrillerState] = useState(initialMovies.concat())

    const addMovie = (thrillerForm) => {
        setThrillerState([
            ...thrillerState,
            {
                ...thrillerForm,
                id:Math.max(...thrillerState.map(p=> p.id + 1))
            }
        ])
    }
    return (
        <>
        <h3>Top 5 must see Thriller movies</h3>
        <ThrillerTable thrillerState={thrillerState} />
        <ThrillerForm addMovie={addMovie} />
        </>
    )
}
