import React, {useState} from 'react'

export default function ThrillerForm({addMovie}) {

    const [thrillerForm, setThrillerForm] = useState({
        name: '',
        year: '',
        rating: ''
    })

    const updateForm = (e) => {
        setThrillerForm(
            {
                ...thrillerForm,
                [e.target.name]:e.target.value
            }
        )
    }

    const activateAddMovie = (thrillerForm) => {
        addMovie(thrillerForm)
        console.log(thrillerForm)
    }


        return (
            <form>
            <label htmlFor='give-name'>Name</label>
            <input id='give-name' type='text' name='name' onChange={updateForm}  />

            <label htmlFor='give-year'>Year</label>
            <input id='give-year' type='number' name='year' onChange={updateForm} />

            <label htmlFor='give-rating'>Rating</label>
            <input id='give-rating' type='number' name='rating' onChange={updateForm} />

            <button type='button' onClick={activateAddMovie}>Add to the list</button>
            </form>
        )
}
