import React, {useState} from 'react'

export default function ThrillerForm({addMovie, thrillerState}) {

    const [thrillerForm, setThrillerForm] = useState({
        name: '',
        year: '',
        rating: ''
    })

    const updateForm = (e) => {
        setThrillerForm(
            {
                ...thrillerForm,
                [e.target.name]:e.target.value
            }
        )
    }

    const activateAddMovie = () => {
        addMovie(thrillerForm)

        setThrillerForm({
            name: '',
            year: '',
            rating: ''
        })
    }
        if (thrillerState.length === 5) {
            return (
                <>
                </>
            )
        }
        else {
     return (
         <form>
         <label htmlFor='give-name'>Name</label>
         <input id='give-name' type='text' name='name' onChange={updateForm} value={thrillerForm.name} />

         <label htmlFor='give-year'>Year</label>
         <input id='give-year' type='number' name='year' onChange={updateForm} value={thrillerForm.year} />

         <label htmlFor='give-rating'>Rating</label>
         <input id='give-rating' type='number' name='rating' onChange={updateForm} value={thrillerForm.rating} />

         <button type='button' onClick={activateAddMovie}>Add to the list</button>
         </form>
        )
        }
     }
