import { useState } from 'react'


export const useFormHooks = () => {

    const [form, setForm] = useState()

    const updateForm = (e) => {
        setForm(
            {
                ...form,
                [e.target.name]: (e.target.type === 'number') ? Number(e.target.value) : e.target.value
            })
    }

        return [form, updateForm]

}


